import {ImageRawGeneratorInterface} from "./ImageRawGenerator";

export type ImageRawGeneratorMockProps = {
  simulateDelay?: boolean;
  simulateDelayMs?: number;
}
export class ImageRawGeneratorMock implements ImageRawGeneratorInterface {

  private props: ImageRawGeneratorMockProps | null = null;

  constructor(props?: ImageRawGeneratorMockProps) {
    this.props = props || null;
  }

  async generateImage(prompt: string): Promise<Buffer<ArrayBuffer>> {
    // simulate delay

    if(this.props?.simulateDelay){
      let defaultSimlatedDelayMs = 5000 + Math.random() * 5000; // 5-10 seconds
      let simlatedDelayMs = defaultSimlatedDelayMs;
      // allow "0" delay ms
      if(this.props?.simulateDelayMs !== undefined){
        simlatedDelayMs = this.props.simulateDelayMs;
      }
      await new Promise(resolve => setTimeout(resolve, simlatedDelayMs));
    }

    // create a dummy image file in buffer here without loading from disk

    let dummyImageBuffer = Buffer.from("", 'base64');
    return dummyImageBuffer;
  }

}
